 * Implements reCAPTCHA bypass for authorized automation tools.
 * 
 * ARCHITECTURE: Prototype (Single-File Injection)
 * AUTHENTICATION: Simple Secret Key (Matches 'test-automation-key-2026')
 * LOGGING: [DI-Automation] prefix
 */

// CONFIGURATION
if ( ! defined( 'DI_AUTOMATION_SECRET_KEY' ) ) {
    // Hardcoded for staging test comfort - in production this comes from env
    define( 'DI_AUTOMATION_SECRET_KEY', 'test-automation-key-2026' );
}

/**
 * Authorization Check
 * Uses static memoization to ensure near-zero latency overhead.
 */
if ( ! function_exists( 'di_ab_is_authorized' ) ) {
    function di_ab_is_authorized() {
        static $is_auth = null;
        if ( $is_auth === null ) {
            $header_value = $_SERVER['HTTP_X_DI_TEST_AUTH'] ?? null;
            // Prototype Phase: Direct string comparison
            $is_auth = ( $header_value && $header_value === DI_AUTOMATION_SECRET_KEY );
        }
        return $is_auth;
    }
}

/**
 * 1. EARLY INIT: Mock Token Injection
 * Satisfies plugins that check $_POST['recaptcha_response'] early.
 */
add_action( 'init', function() {
    if ( di_ab_is_authorized() ) {
        // Inject fake token to satisfy 'presence' checks
        $_REQUEST['recaptcha_response'] = 'AUTOMATION_BYPASS_TOKEN_STAGING';
        $_POST['recaptcha_response']    = 'AUTOMATION_BYPASS_TOKEN_STAGING';

        // Log only on first run to avoid noise
        if ( ! defined( 'DI_AB_LOGGED' ) ) {
            error_log( '[DI-Automation] üîì Bypass Active for Request from: ' . $_SERVER['REMOTE_ADDR'] );
            define( 'DI_AB_LOGGED', true );
        }
    }
}, 1 ); // Priority 1 (Very Early)

/**
 * 2. NETWORK: Google API Interception ("The Nuclear Option")
 * Mocks the HTTP response to google.com/recaptcha to return success.
 */
add_filter( 'pre_http_request', function( $preempt, $args, $url ) {
    // Only intercept if authorized AND targeting Google reCAPTCHA
    if ( di_ab_is_authorized() && strpos( $url, 'google.com/recaptcha' ) !== false ) {

        error_log( '[DI-Automation] üõë Intercepting outbound call to: ' . $url );

        // Return a valid WP_Http response array
        return [
            'response' => [ 
                'code' => 200, 
                'message' => 'OK' 
            ],
            'body'     => json_encode( [
                'success'      => true,
                'score'        => 0.9, // High score for v3
                'action'       => 'submit',
                'challenge_ts' => date( 'c' ),
                'hostname'     => $_SERVER['HTTP_HOST'] ?? 'localhost',
            ] ),
            'cookies'  => [],
            'headers'  => [],
        ];
    }
    return $preempt;
}, 10, 3 );

/**
 * 3. VALIDATION: Remove Captcha Fields
 * Prevents Gravity Forms from trying to validate the captcha field itself.
 */
add_filter( 'gform_pre_validation', function( $form ) {
    if ( di_ab_is_authorized() ) {
        if ( ! isset( $form['fields'] ) || ! is_array( $form['fields'] ) ) {
            return $form;
        }
        foreach ( $form['fields'] as $key => $field ) {
            if ( isset( $field->type ) && in_array( $field->type, [ 'captcha', 'recaptcha' ] ) ) {
                unset( $form['fields'][ $key ] );
                error_log( '[DI-Automation] üóëÔ∏è Removed reCAPTCHA field (' . $field->id . ') from Form ' . $form['id'] );
            }
        }
    }
    return $form;
} );

/**
 * 4. SPAM FILTER: Force Not-Spam
 * Overrides the DI Spam Filter which typically runs at priority 10-11.
 */
add_filter( 'gform_entry_is_spam', function( $is_spam ) {
    if ( di_ab_is_authorized() ) {
        if ( $is_spam ) {
            error_log( '[DI-Automation] üõ°Ô∏è Overriding Spam Detection (Was: True)' );
        }
        return false;
    }
    return $is_spam;
}, 20 ); // Priority 20 (High)


