}

function remove_action($tag, $callback, $priority = 10)
{
    global $wp_actions;
    foreach ($wp_actions as $i => $action) {
        if ($action['tag'] === $tag && $action['callback'] === $callback && $action['priority'] === $priority) {
            unset($wp_actions[$i]);
            $wp_actions = array_values($wp_actions);
            return true;
        }
    }
    return false;
}

