 */
function di_form_buddy_health_check($site, $form_id, $output_mode)
{
    $checks = array(
        'wordpress'   => true,  // Already bootstrapped if we got here
        'gfapi'       => true,  // Already verified if we got here
        'form_exists' => false,
    );

    // Get the form
    $form = \GFAPI::get_form($form_id);
    if (!$form || is_wp_error($form)) {
        $checks['form_exists'] = false;

        $result = array(
            'mode'           => 'health_check',
            'site'           => $site,
            'form_id'        => (int) $form_id,
            'form_name'      => null,
            'status'         => 'fail',
            'field_count'    => 0,
            'required_count' => 0,
            'checks'         => $checks,
        );

        // Text output
        if ($output_mode === 'text') {
            di_form_buddy_log('HEALTH CHECK: ' . $site . ' / Form ' . $form_id);
            di_form_buddy_log('✓ WordPress bootstrapped');
            di_form_buddy_log('✓ GFAPI available');
            di_form_buddy_log('✗ Form does not exist');
            di_form_buddy_log('HEALTH: FAIL');
        }

        return $result;
    }

    $checks['form_exists'] = true;
    $form_name = isset($form['title']) ? $form['title'] : '';

    // Count fields (excluding display-only types)
    $skip_types = di_form_buddy_get_skip_types();
    $field_count = 0;
    $required_count = 0;

    if (isset($form['fields']) && is_array($form['fields'])) {
        foreach ($form['fields'] as $field) {
            $type = isset($field->type) ? $field->type : '';
            if (in_array($type, $skip_types, true)) {
                continue;
            }
            $field_count++;
            if (!empty($field->isRequired)) {
                $required_count++;
            }
        }
    }

    $result = array(
        'mode'           => 'health_check',
        'site'           => $site,
        'form_id'        => (int) $form_id,
        'form_name'      => $form_name,
        'status'         => 'pass',
