function addModalListeners() {
	$(_galleryModal).on('show.bs.modal', function(e) {
		var images = $(e.relatedTarget).data('images');
		populateSliders(images);
	});

	$(_galleryModal).on('hidden.bs.modal', function(e) {
		destroySwipers();
	});
}

function populateSliders(images) {
	buildImagesArray(images).done(function(finalImages) {
		_images = finalImages;
		initializeSwipers();
	}).fail(function() {
		if (typeof console !== 'undefined') {

			console.log("VRP Gallery: No images found");
		}
	});
}

function buildImagesArray(images) {
	var promise = $.Deferred();

	if (images) {
		var filteredImages = images.filter(function(item) {
			return item.indexOf('http') !== -1;

	}

	return promise.promise();
}

