  if(isset($vehicle['special_offer_price']) && $vehicle['special_offer_price'] != '') {
    $vehicle['our_price_label'] = apply_filters('special_offers_pricing_label', 'Special Sale Price:', $vehicle);
    $vehicle['our_price'] = preg_replace('/[^0-9]+/', '', $vehicle['special_offer_price']);
    if(isset($vehicle['msrp']) && $vehicle['msrp'] > 0 ) {
      $vehicle['discounts'] = ($vehicle['msrp'] - (preg_replace('/[^0-9]+/', '', $vehicle['special_offer_price'])));
      $vehicle['total_savings'] = ($vehicle['msrp'] - (preg_replace('/[^0-9]+/', '', $vehicle['special_offer_price'])));
    }

   }
 }
 if(isset($vehicle['special_payment']) && $vehicle['special_payment'] != '' && is_numeric($vehicle['special_payment']['price'])) {
   $vehicle['monthly_payment'] = '$'.number_format(preg_replace('/[^0-9]+/', '', $vehicle['special_payment']['price']));
   $vehicle['monthly_payment_label'] = "/month";
   $vehicle['finance_label'] = apply_filters('special_offers_payment_label', 'Special '.$vehicle['special_payment']['type'].':', $vehicle);
 }
 if(isset($vehicle['special_offer_price_mod']) && (is_null($vehicle['special_offer_price']) || $vehicle['special_offer_price'] == '')) {
  $vehicle['discounts'] = ($vehicle['discounts'] + $vehicle['special_offer_price_mod']);
  $vehicle['total_savings'] = ($vehicle['total_savings'] + $vehicle['special_offer_price_mod']);
}

