function fix_cta_box_height(box_height, window_height) {
  var headerHeight = 0;
  var headers = $('#header,.menu-top:not(#header-vertical),#headerTop,#mini-header,.headerwrap').filter(':visible');
  $(headers).each(function(i, obj){
    if( $(this).css("position") == "fixed" )
      headerHeight = (headerHeight + parseInt($(this).outerHeight(true)));

    if (this.complete && this.naturalHeight) {
        $(this).load();
    }
});

